import React, { useState, useEffect } from 'react';
import './App.css';  // Import the CSS file
import CarList from './CarList';  // Adjust the path as per your project structure

const useCarManagement = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchCars = async () => {
    try {
      const response = await fetch('https://mniprojectback7.onrender.com/v1/api/cars');
      const data = await response.json();
      setCars(data);
    } catch (error) {
      console.error('Error fetching car data:', error);
      setError('Error fetching car data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleAddCar = async (newCar) => {
    try {
      await fetch('https://mniprojectback7.onrender.com/v1/api/cars', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newCar),
      });
      setCars((prevCars) => [...prevCars, newCar]);
    } catch (error) {
      console.error('Error adding car:', error);
      setError('Error adding car. Please try again.');
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);

  return { cars, loading, error, handleAddCar };
};

const App = () => {
  const { cars, loading, error, handleAddCar } = useCarManagement();

  // Example usage of handleAddCar
  const addCar = () => {
    const newCar = { model: 'Example Model', year: 2023, color: 'Blue' };
    handleAddCar(newCar);
  };

  return (
    <div className="app-container">
      <h1 className="app-title">Welcome to the Car Management Page</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <div>
          {/* Replace CarForm with your actual form component */}
          {/* <CarForm onSubmit={handleAddCar} /> */}

          {/* Use your actual CarList component */}
          <CarList cars={cars} />

          {/* Button to demonstrate using handleAddCar */}
          <button onClick={addCar}>Add Example Car</button>
        </div>
      )}
    </div>
  );
};

export default App;
