import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
 const [cars, setCars] = useState([]);
 const [model, setModel] = useState('');
 const [year, setYear] = useState('');
 const [color, setColor] = useState('');

 useEffect(() => {
   async function fetchCars() {
     try {
       const response = await
fetch('http://localhost:3001/v1/api/cars'); // Update the port as
needed
       const data = await response.json();
       console.log(data);
       setCars(data);
     } catch (error) {
       console.error('Error fetching car data:', error);
     }
   }

   fetchCars();
 }, []);

 const handleAddCar = async () => {
   if (!model || !year || !color) {
     alert('Please enter all car details.');
     return;
   }

   try {
     const newCar = {
       model,
       year,
       color,
     };
     await fetch('http://localhost:3001/v1/api/cars', {
       method: 'POST',
       headers: {
         'Content-Type': 'application/json',
       },
       body: JSON.stringify(newCar),
     });
     setCars((prevCars) => [...prevCars, newCar]);
     setModel('');
     setYear('');
     setColor('');
   } catch (error) {
     console.error('Error adding car:', error);
   }
 };

 return (
   <div className="app-container">
     <h1 className="app-title">Welcome to the Car Management Page</h1>

     <div className="app-section">
       <h2>Add a Car</h2>
       <input
         type="text"
         name="model"
         value={model}
         onChange={(e) => setModel(e.target.value)}
         placeholder="Enter car model"
       />
       <input
         type="number"
         name="year"
         value={year}
         onChange={(e) => setYear(e.target.value)}
         placeholder="Enter car year"
       />
       <input
         type="text"
         name="color"
         value={color}
         onChange={(e) => setColor(e.target.value)}
         placeholder="Enter car color"
       />
       <button className="app-button" onClick={handleAddCar}>
         Add Car
       </button>
     </div>

     <div className="app-section">
       <h2>Car List</h2>
       <ul className="app-file-list">
         {cars.map((car, index) => (
           <li key={index}>
             Model: {car.model}, Year: {car.year}, Color: {car.color}
           </li>
         ))}
       </ul>
     </div>
   </div>
 );
}

export default App;
